INSERT INTO `collections` VALUES (1,3,'Лучшие книги 2025','Подборка лучших книг года','2025-03-01','Одобрено'),(2,1,'Классика на все времена','Книги, которые должен прочитать каждый','2025-03-02','Одобрено'),(3,2,'Научная фантастика','Лучшие произведения жанра','2025-03-03','Отказано'),(4,6,'Психология и саморазвитие','Книги для личностного роста','2025-03-04','Одобрено'),(5,7,'Детективы и триллеры','Захватывающие истории','2025-03-05','Одобрено'),(6,8,'Фэнтези и магия','Мир магии и приключений','2025-03-06','Одобрено'),(7,10,'Исторические романы','Погружение в прошлое','2025-03-07','Отказано'),(8,3,'Книги для вдохновения','Мотивирующие произведения','2025-03-08','Одобрено'),(9,1,'Современная проза','Актуальные книги','2025-03-09','Одобрено'),(10,2,'Бизнес и финансы','Книги для успеха','2025-03-10','Одобрено'),(11,6,'Любимые книги детства','Воспоминания из детства','2025-03-11','Одобрено'),(12,7,'Книги о любви','Романтические истории','2025-03-12','Одобрено'),(13,8,'Книги о путешествиях','Вдохновение для новых поездок','2025-03-13','Одобрено'),(14,10,'Книги о науке','Популярная наука для всех','2025-03-14','На рассмотрении'),(15,3,'Книги для души','Произведения, которые трогают сердце','2025-03-15','Отказано'),(16,1,'Русская классика','Лучшие произведения русской литературы','2025-03-16','Одобрено'),(17,2,'Антиутопии','Книги о возможном будущем человечества','2025-03-17','Одобрено'),(18,6,'Психология для жизни','Книги, которые помогут разобраться в себе','2025-03-18','Одобрено'),(19,7,'Фэнтези-бестселлеры','Самые популярные книги в жанре фэнтези','2025-03-19','Одобрено'),(20,8,'Книги, меняющие сознание','Произведения, после которых вы посмотрите на мир иначе','2025-03-20','Одобрено'),(21,10,'Современная проза','Актуальные произведения наших дней','2025-03-21','На рассмотрении'),(22,1,'Книги для саморазвития','Литература для личностного роста','2025-03-22','Одобрено'),(23,2,'Исторические романы','Художественные произведения на основе реальных событий','2025-03-23','Одобрено'),(24,6,'Научно-популярная литература','Сложные научные концепции простым языком','2025-03-24','Одобрено'),(25,7,'Детективы и триллеры','Захватывающие истории с интригующим сюжетом','2025-03-25','Одобрено'),(26,2,'Классика мировой литературы','Эти книги прошли проверку временем, их читают поколениями, а их герои и идеи стали частью нашей культуры. От масштабных эпопей до глубоких философских романов — здесь собраны шедевры, без которых невозможно представить мировую литературу.','2025-05-10','На рассмотрении'),(27,23,'Фэнтези и мистика','Любите погружаться в миры, где возможны чудеса, древние пророчества и тайные силы? В этой подборке — книги, где реальность переплетается с мистикой, а герои сталкиваются с невероятными испытаниями.','2025-05-05','На рассмотрении'),(28,24,'Психология и саморазвитие','Эти книги помогут разобраться в своих эмоциях, выстроить границы, научиться управлять финансами и найти внутреннюю гармонию.','2025-04-28','Одобрено'),(29,15,'Научная фантастика и антиутопии','От жестких антиутопий до размышлений о природе разума — эти книги заставляют задуматься о том, куда движется человечество.','2025-05-11','На рассмотрении'),(30,14,'Детективы и триллеры','Любите головоломные расследования, неожиданные развязки и леденящие душу истории? Эти книги не дадут вам заскучать.','2025-05-01','На рассмотрении'),(31,32,'Книги о любви и чувствах','От легких романтических комедий до глубоких драм о любви — здесь собраны книги, которые заставят вас смеяться, плакать и переживать.','2025-04-30','Одобрено'),(32,17,'Исторические романы и приключения','Эти книги перенесут вас в другую эпоху — от дворцовых заговоров до полей сражений.','2025-04-14','Одобрено'),(33,25,'Книги, меняющие сознание','Эти произведения бросают вызов привычным взглядам, заставляют пересмотреть ценности и задать себе сложные вопросы.','2025-05-01','На рассмотрении'),(34,20,'Современная проза','Эти истории говорят о том, что волнует нас сегодня: отношения, поиск себя, вызовы современного мира.','2025-05-12','На рассмотрении'),(35,29,'Книги для души','Эти книги согревают сердце, дарят утешение и напоминают о самом важном.','2025-05-08','На рассмотрении'),(36,6,'<span class=\"forbidden-word\">Бля</span>, прикол','<p>пу пу пу пу пу</p>','2025-09-29','Обнаружено нарушение');
